import React, { Component } from 'react';
//import logo from '../logo.png';
import Web3 from 'web3';
import './App.css';
import Meme from '../abis/meme.json';

var ipfsClient = require('ipfs-http-client');
var ipfs = ipfsClient({host:'ipfs.infura.io',port:'5001',protocol: 'https' }) ;
class App extends Component {


    async componentWillMount(){
      await this.loadWeb3();
      await this.loadBlockChainData();
    }

    // Get the account 
    // Get the network
    // Get Smart contract
    // Get the Meme Hash 


    async loadBlockChainData(){
      var web3 = window.web3;
      var accounts = await web3.eth.getAccounts();
      console.log(accounts);
      this.setState({account:accounts[0]})
      const networkId= await web3.eth.net.getId();
      const networkData =Meme.networks[networkId];
      console.log(networkId);

      // if(networkId){
      //   //Fetch the smart contract
      //   const abi = Meme.abi;
      //   const address = networkData.address;
      //   const contract = web3.eth.Contract(abi,address);
      //   this.setState({contract:contract})
      //   console.log(contract );
      //   const memHash = await contract.methods.get().call();
      //   console.log(memHash );
      //   this.setState({memeHash:memHash})
      // }
      // else{
      //   window.alert("smart contract nor deployed to dected network");
      // }

    }

    constructor(props){
      super(props);
      this.state={
        account:'',
        buffer:null,
        Contract:null,
        memHash:'QmNZNHWxYqPY57bodafqkzqmXYVu9LE3FteJhMmZGBackw'
      };

      this.filesrc="";

    }

    async loadWeb3(){
      if(window.ethereum){
        window.web3 =new Web3(window.ethereum);
        await window.ethereum.enable();
      }
      if(window.web3){
        window.web3 = new Web3(window.web3.currentProvider);
      }
      else{
        window.alert("Please use metamask");
      }

      // console.log(Web3);
      // var d=new Web3.providers.HttpProvider('http://127.0.0.1:7545');
      // new Web3(d);
      // console.log(Web3.eth);


    }


  captureFile=(event)=>{
      event.preventDefault();
      console.log("file is capture");
      console.log(event);
      console.log(event.target.files[0]);
      var file = event.target.files[0];
      var reader = new window.FileReader();
      reader.readAsArrayBuffer(file);
      reader.onloadend = ()=>{
        this.setState({buffer:Buffer(reader.result)})
        console.log("buffer",Buffer(reader.result));
      }
      //process the file inside here 
  }

  // Hash QmNZNHWxYqPY57bodafqkzqmXYVu9LE3FteJhMmZGBackw
  //url https://ipfs.infura.io/ipfs/QmNZNHWxYqPY57bodafqkzqmXYVu9LE3FteJhMmZGBackw

  onSubmit=(event)=>{
    event.preventDefault();
    console.log("in submit event");

    var name ="Rutvik";


    ipfs.add(name,(error,results)=>{
      //Do Stuff here
      console.log("IPFS RESULT",results[0].hash);
      var hash=results[0].hash;
      if(error){
        console.log(error);
        return;
      }
      //Step 2 is to store file on blockchain
      this.filesrc="https://ipfs.infura.io/ipfs/"+hash;
      console.log(this.filesrc);

      ipfs.get(hash,(error,result)=>{
        console.log(result);
      })

      var y=ipfs.catReadableStream(hash);
      console.log(y);


      this.setState({memHash:hash});
    })

  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            Meme of the day
          </a>
          <ul className="navbar-nav px-3">
            <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
              <small className="text-white">{this.state.account}</small>
            </li>
          </ul>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src= {`https://ipfs.infura.io/ipfs/${this.state.memHash}`}  className="App-logo" alt="logo" />
                </a>
                <br/>
                <br/>
                <h2>Change Meme</h2>
                <br/>
                <form  onSubmit={this.onSubmit}>
                  <input type="file"  onChange={this.captureFile}/>
                  <input type="submit" />
                </form>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
