'use strict'; // Convert a multiaddr to a URI
// Assumes multiaddr is in a format that can be converted to a HTTP(s) URI

exports.toUri = function (ma) {
  var parts = "".concat(ma).split('/');
  var port = getPort(parts);
  return "".concat(getProtocol(parts), "://").concat(parts[2]).concat(port == null ? '' : ':' + port);
};

function getProtocol(maParts) {
  return maParts.indexOf('https') === -1 ? 'http' : 'https';
}

function getPort(maParts) {
  var tcpIndex = maParts.indexOf('tcp');
  return tcpIndex === -1 ? null : maParts[tcpIndex + 1];
}
