import React, { Component } from 'react';
//import logo from '../logo.png';
import Web3 from 'web3';
import './App.css';
var ipfsClient = require('ipfs-http-client');
var ipfs = ipfsClient({host:'ipfs.infura.io',port:'5001',protocol: 'https' }) ;
class App extends Component {


    async componentWillMount(){
      await this.loadWeb3();

    }

    // Get the account 
    // Get the network
    // Get Smart contract
    // Get the Meme Hash 



    constructor(props){
      super(props);
      this.state={
        buffer:null,
        memHash:'QmNZNHWxYqPY57bodafqkzqmXYVu9LE3FteJhMmZGBackw'
      };

      this.filesrc="";

    }

    async loadWeb3(){

      console.log(Web3);


    }


  captureFile=(event)=>{
      event.preventDefault();
      console.log("file is capture");
      console.log(event);
      console.log(event.target.files[0]);
      var file = event.target.files[0];
      var reader = new window.FileReader();
      reader.readAsArrayBuffer(file);
      reader.onloadend = ()=>{
        this.setState({buffer:Buffer(reader.result)})
        console.log("buffer",Buffer(reader.result));
      }
      //process the file inside here 
  }

  // Hash QmNZNHWxYqPY57bodafqkzqmXYVu9LE3FteJhMmZGBackw
  //url https://ipfs.infura.io/ipfs/QmNZNHWxYqPY57bodafqkzqmXYVu9LE3FteJhMmZGBackw

  onSubmit=(event)=>{
    event.preventDefault();
    console.log("in submit event");
    ipfs.add(this.state.buffer,(error,results)=>{
      //Do Stuff here
      console.log("IPFS RESULT",results[0].hash);
      var hash=results[0].hash;
      if(error){
        console.log(error);
        return;
      }
      //Step 2 is to store file on blockchain
      this.filesrc="https://ipfs.infura.io/ipfs/"+hash;
      console.log(this.filesrc);
      this.setState({memHash:hash});
    })

  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            Meme of the day
          </a>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src= {`https://ipfs.infura.io/ipfs/${this.state.memHash}`}  className="App-logo" alt="logo" />
                </a>
                <br/>
                <br/>
                <h2>Change Meme</h2>
                <br/>
                <form  onSubmit={this.onSubmit}>
                  <input type="file"  onChange={this.captureFile}/>
                  <input type="submit" />
                </form>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
