  onSubmit=(event)=>{
    event.preventDefault();
    //console.log("in submit event");


    var myObj = {
      "name":"John",
      "age":30,
      "cars": {
        "car1":"Ford",
        "car2":"BMW",
        "car3":"Fiat"
      }
     }


    ipfs.add(myObj,(error,results)=>{
      //Do Stuff here
      //console.log("IPFS RESULT",results[0].hash);
      console.log(results);
      console.log(error);
      if(error){
        console.log(error);
        return;
      }
      //Step 2 is to store file on blockchain
